import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  SafeAreaView,
  SectionList,
  TouchableOpacity,
  Alert,
  ActivityIndicator,
  Share
} from 'react-native';
import { MaterialIcons } from '@expo/vector-icons';
import * as FileSystem from 'expo-file-system';
import * as Sharing from 'expo-sharing';
import { attendanceService } from '../lib/api';

// Define the structure for an attendance record - matches your database/API response
interface AttendanceRecord {
  id?: number;
  student_code_apogee: string;
  exam_room_id: number;
  status: string;
  course?: string | null;
  attended_at: string;
  notes?: string | null;
  created_at?: string;
  updated_at?: string;
  student?: {
    nom: string;
    prenom: string;
    code_apogee: string;
    cne: string;
  };
  exam_room?: {
    name: string;
    location?: string;
  };
}

// Assure-toi que cette URL correspond à l'adresse IP locale et au port de ton serveur Laravel
const API_URL = 'http://192.168.1.38:8000/api'; // <--- Vérifie que cette IP est correcte!

export default function ListScreen() {
  const [attendanceList, setAttendanceList] = useState<AttendanceRecord[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // State for statistics
  const [presentCount, setPresentCount] = useState(0);
  const [absentCount, setAbsentCount] = useState(0);

  // Load attendance data from API
  useEffect(() => {
    const fetchAttendances = async () => {
      setIsLoading(true);
      setError(null);
      try {
        console.log('1. Début du chargement des données...');
        
        const response = await attendanceService.getAllAttendances();
        console.log('2. Réponse reçue du serveur:', response);
        
        const data = response.data;
        console.log('3. Données reçues:', data);
        
        let fetchedList: AttendanceRecord[] = [];
        if (data) {
          if (Array.isArray(data)) {
            console.log('4a. Données reçues comme tableau direct');
            fetchedList = data;
          } else if (data.data && Array.isArray(data.data)) {
            console.log('4b. Données reçues dans data.data');
            fetchedList = data.data;
          } else {
            console.error('4c. Structure de données invalide:', data);
            setError("Structure de données reçues invalide.");
            return;
          }
          
          console.log('5. Liste finale à afficher:', fetchedList);
          setAttendanceList(fetchedList);

          // Calculate statistics
          const present = fetchedList.filter(record => record.status === 'présent' || record.status === 'present').length;
          const absent = fetchedList.filter(record => record.status === 'absent').length;
          setPresentCount(present);
          setAbsentCount(absent);

        } else {
          console.log('5b. Aucune donnée reçue');
          setAttendanceList([]);
        }
      } catch (error: any) {
        console.error('6. Erreur lors du chargement:', error);
        setError("Impossible de charger les données de présence depuis le serveur.");
        if (error.message === 'Network Error') {
          setError("Erreur réseau: Impossible de se connecter au serveur.");
        }
      } finally {
        setIsLoading(false);
      }
    };

    fetchAttendances();

    const intervalId = setInterval(() => {
      fetchAttendances();  // Rafraîchissement toutes les 15 secondes
    }, 15000);
  
    return () => clearInterval(intervalId);
  }, []);


  // Export attendance data as CSV
  const exportToCSV = async () => {
    if (attendanceList.length === 0) {
      Alert.alert('Information', "Aucune donnée à exporter.");
      return;
    }

    const headers = ["Nom", "Prenom", "Code Apogee", "CNE", "Status"];
    const csvRows = [
      headers.join(','),
      ...attendanceList.map(row =>
        [
          `"${row.student?.nom || ''}"`,
          `"${row.student?.prenom || ''}"`,
          `"${row.student?.code_apogee || ''}"`,
          `"${row.student?.cne || ''}"`,
          `"${row.status}"`,
        ].join(',')
      )
    ];

    const csvString = csvRows.join('\n');
    
    try {
      // Créer un nom de fichier avec la date actuelle
      const fileName = `attendance_${new Date().toISOString().split('T')[0]}.csv`;
      
      // Chemin temporaire pour créer le fichier
      const tempPath = `${FileSystem.cacheDirectory}${fileName}`;
      
      // Écrire le fichier CSV temporairement
      await FileSystem.writeAsStringAsync(tempPath, csvString, {
        encoding: FileSystem.EncodingType.UTF8
      });

      // Vérifier si le partage est disponible
      const isAvailable = await Sharing.isAvailableAsync();
      
      if (isAvailable) {
        // Partager le fichier
        await Sharing.shareAsync(tempPath, {
          mimeType: 'text/csv',
          dialogTitle: 'Enregistrer le fichier CSV',
          UTI: 'public.comma-separated-values-text'
        });
      } else {
        // Si le partage n'est pas disponible, proposer de télécharger
        Alert.alert(
          'Téléchargement',
          'Voulez-vous télécharger le fichier CSV ?',
          [
            {
              text: 'Annuler',
              style: 'cancel'
            },
            {
              text: 'Télécharger',
              onPress: async () => {
                try {
                  const downloadPath = `${FileSystem.documentDirectory}Download/${fileName}`;
                  await FileSystem.copyAsync({
                    from: tempPath,
                    to: downloadPath
                  });
                  Alert.alert('Succès', 'Le fichier a été téléchargé dans le dossier Download de l\'application.');
                } catch (error) {
                  Alert.alert('Erreur', 'Impossible de télécharger le fichier.');
                }
              }
            }
          ]
        );
      }

      // Supprimer le fichier temporaire
      await FileSystem.deleteAsync(tempPath);

    } catch (error) {
      console.error('Error saving CSV file:', error);
      Alert.alert('Erreur', "Impossible de télécharger le fichier CSV.");
    }
  };

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>Liste des présences</Text>
        <TouchableOpacity 
          style={styles.exportButton}
          onPress={exportToCSV}
        >
          <MaterialIcons name="file-download" size={24} color="#7C3AED" />
        </TouchableOpacity>
      </View>

      {isLoading ? (
        <View style={styles.loadingContainer}>
          <ActivityIndicator size="large" color="#7C3AED" />
          <Text style={styles.loadingText}>Chargement des données...</Text>
        </View>
      ) : error ? (
        <View style={styles.errorContainer}>
          <MaterialIcons name="error-outline" size={48} color="#EF4444" />
          <Text style={styles.errorText}>{error}</Text>
        </View>
      ) : (
        <View style={styles.statsContainer}>
          <Text style={styles.statText}>Présents: {presentCount}</Text>
          <Text style={styles.statText}>Absents: {absentCount}</Text>
        </View>
      )}

      {attendanceList.length === 0 ? (
        <View style={styles.emptyList}>
          <MaterialIcons name="info-outline" size={48} color="#6B7280" />
          <Text style={styles.emptyText}>
            Aucun enregistrement trouvé.
          </Text>
        </View>
      ) : (
        <SectionList
          sections={[
            {
              title: 'Liste des étudiants',
              data: attendanceList
            }
          ]}
          renderItem={({ item }: { item: AttendanceRecord }) => {
            if (!item) return null;
            
            return (
              <View style={styles.listItem}>
                <View style={styles.itemInfo}>
                  <Text style={styles.itemName}>
                    {item.student?.prenom ? `${item.student.prenom} ` : ''}{item.student?.nom || ''}
                  </Text>
                  <View style={styles.itemDetails}>
                    <Text style={styles.itemDetail}>
                      Code: {item.student?.code_apogee || 'N/A'}
                    </Text>
                    <Text style={styles.itemDetail}>
                      CNE: {item.student?.cne || 'N/A'}
                    </Text>
                  </View>
                  {item.status === 'présent' && item.attended_at && (
                    <Text style={styles.itemTimestamp}>
                      {new Date(item.attended_at).toLocaleString()}
                    </Text>
                  )}
                  {item.notes && (
                    <Text style={styles.itemDetail}>
                      Notes: {item.notes}
                    </Text>
                  )}
                </View>
                <View style={styles.itemStatus}>
                  <Text style={[
                    styles.statusBadge,
                    item.status === 'présent' || item.status === 'present' ? styles.statusPresent : styles.statusAbsent,
                  ]}>
                    {(item.status || 'N/A').toUpperCase()}
                  </Text>
                </View>
              </View>
            );
          }}
          renderSectionHeader={({ section: { title } }) => (
            <View style={styles.sectionHeader}>
              <Text style={styles.sectionHeaderText}>{title}</Text>
            </View>
          )}
          keyExtractor={(item: AttendanceRecord, index: number) => 
            item?.id ? `${item.id}-${index}` : `item-${index}`
          }
          contentContainerStyle={styles.listContent}
          showsVerticalScrollIndicator={false}
          stickySectionHeadersEnabled={false}
        />
      )}
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f9fafb',
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: 16,
    paddingTop: 40,
    paddingBottom: 8,
    backgroundColor: '#ffffff',
    borderBottomWidth: 1,
    borderBottomColor: '#e5e7eb',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#EF4444',
