import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  FlatList,
  TouchableOpacity,
  ActivityIndicator,
  SafeAreaView,
} from 'react-native';
import { examService } from '../lib/api';
import { useNavigation } from '@react-navigation/native';
import type { NativeStackNavigationProp } from '@react-navigation/native-stack';

type RootStackParamList = {
  ExamSelection: undefined;
  ExamDetails: { examId: number };
};

type Navigation = NativeStackNavigationProp<RootStackParamList, 'ExamSelection'>;

type Exam = {
  id: number;
  name: string;
  matiere: string;
  date: string;
  heure_debut: string;
  heure_fin: string;
};

interface ApiExamResponse {
  id: number;
  name?: string;
  intitule: string;
  date: string;
  time_range: string;
  description?: string;
  details?: {
    code?: string;
    matiere?: string;
    filiere?: string;
    niveau?: string;
    groupe?: string;
    enseignant?: string;
    salle?: string;
    heure_debut?: string;
    heure_fin?: string;
    [key: string]: any;
  };
  [key: string]: any;
}

export default function ExamSelectionScreen() {
  const navigation = useNavigation<Navigation>();
  const [exams, setExams] = useState<Exam[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchExams = async () => {
      try {
        setLoading(true);
        setError(null);

        // Utiliser directement axios pour éviter les problèmes de typage
        const response = await axios.get('http://localhost:8000/api/exams');

        if (response.data && Array.isArray(response.data)) {
          const formattedExams = response.data.map((exam: any) => ({
            id: exam.id,
            name: exam.intitule || 'Sans titre',
            matiere: exam.matiere || 'Matière non spécifiée',
            date: exam.date,
            heure_debut: exam.heure_debut || '',
            heure_fin: exam.heure_fin || ''
          }));

          console.log('Examens chargés:', formattedExams);
          setExams(formattedExams);
        } else {
          throw new Error('Format de réponse invalide');
        }
      } catch (error) {
        console.error('Erreur lors du chargement des examens:', error);
        // Données de test en cas d'échec
        const mockExams: Exam[] = [
          { 
            id: 1, 
            name: 'Examen de Mathématiques',
            matiere: 'Mathématiques Avancées',
            date: '2024-06-15',
            heure_debut: '09:00',
            heure_fin: '12:00'
          },
          { 
            id: 2, 
            name: 'Examen de Physique',
            matiere: 'Physique Quantique',
            date: '2024-06-16',
            heure_debut: '14:00',
            heure_fin: '17:00'
          }
        ];
        setExams(mockExams);
        setError('Données de démonstration (erreur de connexion au serveur)');
      } finally {
        setLoading(false);
      }
    };

    fetchExams();
  }, []);

  const handleSelectExam = (exam: Exam) => {
    // Navigation vers les détails de l'examen si nécessaire
    // Pour l'instant, on ne fait rien ou on peut afficher une alerte
    console.log('Examen sélectionné:', exam);
  };

  if (loading) {
    return (
      <View style={styles.centered}>
        <ActivityIndicator size="large" color="#7C3AED" />
      </View>
    );
  }

  if (error) {
    return (
      <View style={styles.centered}>
        <Text style={styles.errorText}>{error}</Text>
      </View>
    );
  }

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>Sélectionner un examen</Text>
      </View>

      <FlatList
        data={exams}
        keyExtractor={(item) => item.id.toString()}
        contentContainerStyle={styles.listContainer}
        renderItem={({ item }) => (
          <TouchableOpacity
            style={styles.examCard}
            onPress={() => handleSelectExam(item)}
          >
            <View style={styles.examInfo}>
              <Text style={styles.examName}>{item.name}</Text>
              <Text style={styles.examMatiere}>{item.matiere}</Text>
              <View style={styles.examDateContainer}>
                <Text style={styles.examDate}>
                  {new Date(item.date).toLocaleDateString('fr-FR', {
                    day: '2-digit',
                    month: '2-digit',
                    year: 'numeric'
                  })}
                </Text>
                <Text style={styles.examTime}>
                  {item.heure_debut} - {item.heure_fin}
                </Text>
              </View>
            </View>
          </TouchableOpacity>
        )}
        ListEmptyComponent={
          <View style={styles.centered}>
            <Text>Aucun examen disponible</Text>
          </View>
        }
      />
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f9fafb',
  },
  header: {
    padding: 20,
    borderBottomWidth: 1,
    borderBottomColor: '#e5e7eb',
    backgroundColor: 'white',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#111827',
  },
  listContainer: {
    padding: 16,
  },
  examCard: {
    backgroundColor: 'white',
    borderRadius: 12,
    padding: 16,
    marginBottom: 12,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 1 },
    shadowOpacity: 0.1,
    shadowRadius: 3,
    elevation: 2,
  },
  examInfo: {
    flex: 1,
  },
  examName: {
    fontSize: 18,
    fontWeight: '600',
    color: '#111827',
    marginBottom: 8,
  },
  examMatiere: {
    fontSize: 14,
    color: '#4B5563',
    marginBottom: 8,
    fontWeight: '500',
  },
  examDateContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginTop: 4,
  },
  examDate: {
    fontSize: 14,
    color: '#6B7280',
  },
  examTime: {
    fontSize: 14,
    color: '#6B7280',
    fontWeight: '500',
  },
  centered: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#f9fafb',
  },
  errorText: {
    color: '#EF4444',
    fontSize: 16,
    textAlign: 'center',
    padding: 20,
  },
  examDescription: {
    fontSize: 13,
    color: '#6B7280',
    fontStyle: 'italic',
    marginBottom: 4,
  },
});
