import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  FlatList,
  TouchableOpacity,
  ActivityIndicator,
  SafeAreaView,
} from 'react-native';
import { examService } from '../lib/api';
import { useNavigation } from '@react-navigation/native';
import type { NativeStackScreenProps } from '@react-navigation/native-stack';

type RootStackParamList = {
  ExamSelection: undefined;
  ExamDetails: { examId: number };
};

type Props = NativeStackScreenProps<RootStackParamList, 'ExamSelection'>;
type Navigation = Props['navigation'];

type Exam = {
  id: number;
  name: string;
  matiere: string;
  date: string;
  heure_debut: string;
  heure_fin: string;
};

interface ApiExamResponse {
  id: number;
  name?: string;
  intitule: string;
  date: string;
  time_range: string;
  description?: string;
  details?: {
    code?: string;
    matiere?: string;
    filiere?: string;
    niveau?: string;
    groupe?: string;
    enseignant?: string;
    salle?: string;
    heure_debut?: string;
    heure_fin?: string;
    [key: string]: any;
  };
  [key: string]: any;
}

export default function ExamSelectionScreen() {
  const navigation = useNavigation<ExamSelectionScreenNavigationProp>();
  const [exams, setExams] = useState<Exam[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchExams = async () => {
      try {
        setLoading(true);
        setError(null);
        console.log('Fetching exams from API...');

        try {
          // Try to fetch from API first
          const response = await examService.getAllExams();
          console.log('API Response:', response);

          if (response && Array.isArray(response)) {
            // Format the API response
            const formattedExams = response.map((exam: any) => ({
              id: exam.id,
              name: exam.name || exam.intitule || 'Sans titre',
              matiere: exam.matiere || exam.details?.matiere || 'Matière non spécifiée',
              date: exam.date || new Date().toISOString().split('T')[0],
              heure_debut: exam.heure_debut || exam.details?.heure_debut || '',
              heure_fin: exam.heure_fin || exam.details?.heure_fin || ''
            }));

            console.log('Formatted exams:', formattedExams);
            setExams(formattedExams);
          } else {
            console.warn('Unexpected API response format, using fallback data');
            throw new Error('Invalid API response format');
          }
        } catch (apiError) {
          console.error('Error fetching from API, using fallback data:', apiError);

                  // Données de test simplifiées
          const mockExams: Exam[] = [
            { 
              id: 1, 
              name: 'Examen de Mathématiques',
              matiere: 'Mathématiques Avancées',
              date: '2024-06-15',
              heure_debut: '09:00',
              heure_fin: '12:00'
            },
            { 
              id: 2, 
              name: 'Examen de Physique',
              matiere: 'Physique Quantique',
              date: '2024-06-16',
              heure_debut: '14:00',
              heure_fin: '17:00'
            }
          ];

          console.log('Using fallback mock data:', mockExams);
          setExams(mockExams);
          setError('Données de démonstration (erreur de connexion au serveur)');
        }
      } catch (error) {
        console.error('Unexpected error in fetchExams:', error);
        setError('Une erreur inattendue est survenue');
      } finally {
        setLoading(false);
      }
    };

    fetchExams();
  }, []);

  const handleSelectExam = (exam: Exam) => {
    // Navigation vers les détails de l'examen si nécessaire
    // Pour l'instant, on ne fait rien ou on peut afficher une alerte
    console.log('Examen sélectionné:', exam);
  };

  if (loading) {
    return (
      <View style={styles.centered}>
        <ActivityIndicator size="large" color="#7C3AED" />
      </View>
    );
  }

  if (error) {
    return (
      <View style={styles.centered}>
        <Text style={styles.errorText}>{error}</Text>
      </View>
    );
  }

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>Sélectionner un examen</Text>
      </View>

      <FlatList
        data={exams}
        keyExtractor={(item) => item.id.toString()}
        contentContainerStyle={styles.listContainer}
        renderItem={({ item }) => (
          <TouchableOpacity
            style={styles.examCard}
            onPress={() => handleSelectExam(item)}
          >
            <View style={styles.examInfo}>
              <Text style={styles.examName}>{item.name}</Text>
              <Text style={styles.examMatiere}>{item.matiere}</Text>
              <View style={styles.examDateContainer}>
                <Text style={styles.examDate}>
                  {new Date(item.date).toLocaleDateString('fr-FR', {
                    day: '2-digit',
                    month: '2-digit',
                    year: 'numeric'
                  })}
                </Text>
                <Text style={styles.examTime}>
                  {item.heure_debut} - {item.heure_fin}
                </Text>
              </View>
            </View>
          </TouchableOpacity>
        )}
        ListEmptyComponent={
          <View style={styles.centered}>
            <Text>Aucun examen disponible</Text>
          </View>
        }
      />
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f9fafb',
  },
  header: {
    padding: 20,
    borderBottomWidth: 1,
    borderBottomColor: '#e5e7eb',
    backgroundColor: 'white',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#111827',
  },
  listContainer: {
    padding: 16,
  },
  examCard: {
    backgroundColor: 'white',
    borderRadius: 12,
    padding: 16,
    marginBottom: 12,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 1 },
    shadowOpacity: 0.1,
    shadowRadius: 3,
    elevation: 2,
  },
  examInfo: {
    flex: 1,
  },
  examName: {
    fontSize: 18,
    fontWeight: '600',
    color: '#111827',
    marginBottom: 8,
  },
  examMatiere: {
    fontSize: 14,
    color: '#4B5563',
    marginBottom: 8,
    fontWeight: '500',
  },
  examDateContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginTop: 4,
  },
  examDate: {
    fontSize: 14,
    color: '#6B7280',
  },
  examTime: {
    fontSize: 14,
    color: '#6B7280',
    fontWeight: '500',
  },
  centered: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#f9fafb',
  },
  errorText: {
    color: '#EF4444',
    fontSize: 16,
    textAlign: 'center',
    padding: 20,
  },
  examDescription: {
    fontSize: 13,
    color: '#6B7280',
    fontStyle: 'italic',
    marginBottom: 4,
  },
});
